// import logo from './logo.svg';
import './App.css';

function App() {
  let state=0;
$(document).ready(function(){
    $(".power").click(function(){
          $(".record").toggleClass('on');
          $(".stick").toggleClass('play');
          if(state==0){
              setTimeout(function(){
               $(".mysong")[0].play();
              },1000);
              state=1;
          }
          else{
              $(".mysong")[0].pause();
              state=0;
          }
          $(".slider").change(function(){
              $(".mysong")[0].volume=this.value;
          });
      });

});
  return (
    <>
    <div className="bg"></div>
    <div className=" flex items-center justify-center bg-emerald-900">
     <div className=" w-full h-full sm:w-[60%] sm:h-[60%] p-5 rounded-lg bg-zinc-700 shadow-xl fixed">
      <div className=" flex flex-col sm:flex-row h-[30px] text-center px-5 gap-3 sm:gap-0 ">
      <div className='text-3xl font-bold text-emerald-700 cursor-pointer w-auto'>Developer</div>
        <div className="flex w-full h-full justify-center items-center gap-5 sm:gap-10 pt-1">
        <span className="text-lg text-neutral-300 font-bold hover:text-green-700 transition-all hover:transition-all cursor-pointer w-auto">Home</span>
        <span className="text-lg text-neutral-300 font-bold hover:text-green-700 transition-all hover:transition-all cursor-pointer w-auto">About</span>
        <span className="text-lg text-neutral-300 font-bold hover:text-green-700 transition-all hover:transition-all cursor-pointer w-auto">Skill</span>
        <span className="text-lg text-neutral-300 font-bold hover:text-green-700 transition-all hover:transition-all cursor-pointer w-auto">Experience</span>
        </div>
        <span className=' bg-green-600 hover:bg-emerald-900 rounded-lg text-neutral-600 font-bold hover:text-neutral-300 transition-all hover:transition-all cursor-pointer w-auto py-1 px-2'>Contact</span>
      </div>
      <div className="w-full h-full flex items-center justify-center">
      <div className="w-1/2 h-full flex items-center ">
      <div class="glitch shadow-xl">  
    </div>
      </div>
      <div className="w-1/2 h-[80%] flex flex-col  justify-center gap-5">
      <h1
      class="w-[55%] tracking-wider animate-typing overflow-hidden whitespace-nowrap border-r-4 border-r-white pr-5 text-3xl text-neutral-300 font-bold h-8 ">
      Bayramov Muhammad
    </h1>
    <span className='text-lg tracking-wide text-neutral-300 font-bold  cursor-pointer w-auto'>As a creative programmer, I worked as a developer for more than 2 years and gained experience on real projects. I'm happy to spend most of my time writing code. I think that doing research is the most enjoyable part of my job.</span>
    <div class="player">

                <div class="record">
                    <div class="label"></div>
                </div>
                <div class="stick">
                    <div class="screw"></div>
                </div>
                <button class="power"></button>
                <div class="slidecontainer">
                  <input type="range" class="slider" min="0" max="1" step="0.1" value="0.7"/>
            </div>
            </div>
            <audio loop class="mysong" src="./img/Get lucky  _ Slowed Remix _ Tik Tok Song _ Lyrics (1).mp3" preload="autoplay"></audio>
      </div>
      </div>

     </div>
    </div>
    </>
  );
}

export default App;
