// import logo from './logo.svg';
import { useState } from 'react';
import './App.css';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';



function App() {
 const [section, setSection] = useState(0)

console.log(section)

const sectionChange = (number)=>{
  setSection(number)
}

  return (
    <>
    <div className="none sm: block bg"></div>
    <div className=" flex items-center justify-center bg-emerald-900">
     <div className=" w-full h-full sm:min-w-[900px] sm:min-h-[600px] sm:w-[60%] sm:h-[60%] p-5 rounded-lg bg-zinc-700 shadow-xl fixed">

      <div className=" flex flex-col sm:flex-row h-[150px] sm:h-[30px] text-center px-5 gap-3 sm:gap-0 ">
      <div className='text-3xl font-bold text-emerald-700 cursor-default w-auto'>Developer</div>
        <div className="flex w-full h-full justify-center items-center gap-5 sm:gap-10 pt-1">
        <span className={` ${section && section === 0 ? 'text-green-700' : 'text-neutral-200'} sm:text-xl text-neutral-200 font-bold hover:border-b hover:border-gray-500 hover:text-green-700 transition-all hover:transition-all cursor-pointer w-auto`}  >Home</span>
        <span className={` ${section && section === 1 ? 'text-green-700' : 'text-neutral-200'} sm:text-xl text-neutral-200 font-bold hover:border-b hover:border-gray-500 hover:text-green-700 transition-all hover:transition-all cursor-pointer w-auto`} >About</span>
        <span className={` ${section && section === 2 ? 'text-green-700' : 'text-neutral-200'} sm:text-xl text-neutral-200 font-bold hover:border-b hover:border-gray-500 hover:text-green-700 transition-all hover:transition-all cursor-pointer w-auto`} >Skill</span>
        <span className={` ${section && section === 3 ? 'text-green-700' : 'text-neutral-200'} sm:text-xl text-neutral-200 font-bold hover:border-b hover:border-gray-500 hover:text-green-700 transition-all hover:transition-all cursor-pointer w-auto`}>Experience</span>
        </div>
        <span className='text-xl sm:text-sm bg-emerald-900  hover:bg-emerald-600 rounded-lg text-neutral-300 font-bold hover:text-neutral-600 transition-all hover:transition-all cursor-pointer w-auto pt-3 sm:pt-1 py-1 px-3' >Contact</span>
      </div>
      <div className="w-full h-full sm:gap-16 flex flex-col sm:flex-row items-center justify-center">
      <div className=" w-[80%] h-[25%] my-10 sm:my-0 sm:w-[40%] sm:h-[80%] sm:w-1/2  glitch shadow-2xl rounded-lg bg-cover bg-no-repeat bg-center w-full">  
    </div>
      <div className="sm:w-1/2 h-[80%] flex flex-col justify-start sm:justify-center gap-5">
      <h1
      className="w-[333px] tracking-wider animate-typing overflow-hidden whitespace-nowrap border-r-4 border-r-white pr-5 text-3xl text-neutral-300 font-bold h-[30px] sm:min-h-[30px] sm:h-[75px]">
      Bayramov Muhammad
    </h1>
    <span className='text-md text-neutral-400 font-bold h-1 pb-5'>Android & Web <span className='text-emerald-700'>Developer</span></span>
    <span className='text-md sm:text-lg tracking-wide text-neutral-300 font-bold   w-auto h-auto mb-5 sm:mb-0'>As a creative programmer, I worked as a developer for more than 2 years and gained experience on real projects. I'm happy to spend most of my time writing code. I think that doing research is the most enjoyable part of my job.</span>
    <div className="flex flex-col items-center justify-end h-[100px] sm:h-full w-full">
      <span className='h-6 text-sm sm:text-lg text-neutral-500 w-full mb-2 '>Play the music and look at the site.. :)</span>
      <AudioPlayer
          src="/getLucky.mp3"
          volume={0.3}
          // Try other props!
        />
    </div>

      </div>
      </div>

     </div>
    </div>
    </>
  );
}

export default App;
